mapper.select:
select * from join_info

	
	jiNum = #{jiNum}
	
	
	jiName like '%'||#{jiName}||'%'

mapper.update:
update join_info
jiId = #{jiId}, jiPwd = #{jiPwd}, jiLocal = #{jiLocal}, jiName = #{jiName}, jiEmail = #{jiEmail}, jiGender = #{jiGender}, jiBirth = #{jiBirth}, jiMobile = #{jiMobile}, jiAddress = #{jiAddress}
where jiNum=#{jiNum}
